import React from 'react';
import './App.css';
import * as io from 'socket.io-client';
//import Table from 'react-bootstrap/Table'
import 'bootstrap/dist/css/bootstrap.min.css';


class App extends React.Component{
  constructor() {
    super();

    this.state = {
      exchange: {},
      stocks: {}
    };
  }

  getInitialState(stock) {
    console.log(stock)
    let copyStocks = { ...this.state.stocks };
    copyStocks[stock['ticker']] = {
      'company_name': stock['company_name'],
      'country': stock['country'],
      'total_volume': 0,
      'higher': 0,
      'lower': 0,
      'last_price': 0,
      'porentual_change': 0
    };

    this.setState({stocks: copyStocks});
    console.log(this.state.stocks)
  }

  componentDidMount() {
    var socket = io('wss://le-18262636.bitzonte.com', {
      path: '/stocks'
    });
    socket.emit('EXCHANGES');
    socket.on('EXCHANGES', (data) => {
      console.log(data);
      //this.setState({exchange: data})
    });
    socket.emit('STOCKS');
    socket.on('STOCKS', (data) => {
      for (const num in data) {
        //console.log(data[num]);
        this.getInitialState(data[num]);
      }
      //this.setState({stocks: data})
