import React from 'react';
import './App.css';
import * as io from 'socket.io-client';
//import Table from 'react-bootstrap/Table'
// import 'bootstrap/dist/css/bootstrap.min.css';


class App extends React.Component{
  constructor() {
    super();

    this.state = {
      exchange: {},
      stocks: {},
      ready: false,
      first: true
    };
  }

  async getInitialStock(socket) {
    let copyStocks = {};
    socket.emit('STOCKS');
    await socket.on('STOCKS', (data) => {
      for (const num in data) {
        copyStocks[data[num]['ticker']] = {
          'company_name': data[num]['company_name'],
          'country': data[num]['country'],
          'total_volume': 0,
          'higher': 0,
          'lower': 0,
          'last_price': 0,
          'porentual_change': 0,
          'time': [],
          'price': []
        };
      }
      this.setState({ stocks: copyStocks, ready: true });
      console.log(this.state)
      console.log("termine")

    });
  }

  updateStock(socket) {

    console.log('updateStock');

    socket.on('UPDATE',(data) => {
      if (this.state.ready){
      console.log(data)
      let copyStocks = { ...this.state.stocks };
      copyStocks[data['ticker']]['price'].push(data['value']);
      copyStocks[data['ticker']]['time'].push(data['time']);
      this.setState({ stocks: copyStocks });
      }
    });
  }

  componentDidMount() {
    var socket = io('wss://le-18262636.bitzonte.com', {
      path: '/stocks'
    });
    this.getInitialStock(socket);
    this.updateStock(socket)
 }


  render () {
    console.log(this.state.stocks)
    if (this.state.ready) {
      const { stocks } = this.state.stocks;
      return (
        <div className="App">
          <h1>Acción</h1>
          {
              Object.keys(stocks).map((key, index) => ( 
          <p key={index}>{key['country_name']}</p> 
            ))
          }
        </div>
      );
    } else { 
      return( 
        <div className="App">
          <h1>Acción</h1>
        </div>
      ); 
    }
  }

} 

export default App;
