import React from 'react';
import './App.css';
import * as io from 'socket.io-client';
//import Table from 'react-bootstrap/Table'
import 'bootstrap/dist/css/bootstrap.min.css';


class App extends React.Component{
  constructor() {
    super();

    this.state = {
      exchange: {},
      stocks: {}
    };
  }

  getInitialStock(data) {
    //console.log(data)
    let copyStocks = { ...this.state.stocks };
    copyStocks[data['ticker']] = {
      'company_name': data['company_name'],
      'country': data['country'],
      'total_volume': 0,
      'higher': 0,
      'lower': 0,
      'last_price': 0,
      'porentual_change': 0,
      'time': [],
      'price': []
    };

    this.setState({stocks: copyStocks});
    //console.log(this.state.stocks)
  }

  updateStock(data) {
    let copyStocks ={ ...this.state.stocks };
    copyStocks[data['ticker']]['time'].push(data['time']) ;
    copyStocks[data['ticker']]['price'].push(data['value']) ;
    this.setState({ stocks: copyStocks });
  }

  componentDidMount() {
    var socket = io('wss://le-18262636.bitzonte.com', {
      path: '/stocks'
    });
   // socket.emit('EXCHANGES');
    //socket.on('EXCHANGES', (data) => {
     // console.log(data);
      //this.setState({exchange: data})
    //});
    socket.emit('STOCKS');
    socket.on('STOCKS', (data) => {
      for (const num in data) {
        this.getInitialStock(data[num]);
      }
    });
    // {ticker: "SNAP", time: 1589552842327, value: 11.28}
   socket.on('UPDATE',(data) => {
     console.log(data);
     this.updateStock(data);
    });
  }

  render () {
    //console.log(this.state);
    return (
      <div className="App">
        <h1>Acci√≥n</h1>
      </div>
    );
  }
} 

export default App;
