import React from 'react';
import './styles/App.css';
import * as io from 'socket.io-client';

import { 
  GridList, 
  GridListTile, 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableRow 
} from '@material-ui/core/';

import {
  Line,
  LineChart,
  XAxis,
  YAxis,
  Tooltip, 
  CartesianGrid
} from 'recharts';

class App extends React.Component{
  constructor() {
    super();

    this.state = {
      exchange: {},
      stocks: {},
      ready_stock: false,
      ready_exchange: false,
      on: false
    };
  }

  getInitialStock(socket) {
    console.log('hola')
    let copyStocks = {};
    socket.emit('STOCKS');
    socket.on('STOCKS', (data) => {
      console.log(data)
      for (const num in data) {

        copyStocks[data[num]['ticker']] = {
          'company_name': data[num]['company_name'],
          'country': data[num]['country'],
          'total_volume': 0,
          'higher': 0,
          'lower': 0,
          'last_price': 0,
          'porcentual_change': 0,
          'data': [],
        };
      }
      this.setState({ stocks: copyStocks, ready_stock: true });
    });
  }

  getInitialExchange(socket){
    let copyExchange = {}
    let stocks = {}
    socket.emit('STOCKS')
    socket.on('STOCKS', (data) => {
      stocks = data
    });
    socket.emit('EXCHANGES')
    socket.on('EXCHANGES', (data) => {

      for (const num in data) {
        copyExchange[data[num]['exchange_ticker']] = {
          'company_ticker': [],
          'exchange_name': data[num]['name'],
          'country': data[num]['country'],
          'stocks':[...data[num]['listed_companies']],
          'total_volume': 0,
          'sell_volume': 0,
          'buy_volume': 0,
          'quantity': ([...data[num]['listed_companies']]).length,
          'participation': 0
        }
        for (var i in stocks){
          if (data[num]['listed_companies'].indexOf(stocks[i]['company_name']) >= 0){
            copyExchange[data[num]['exchange_ticker']]['company_ticker'].push(stocks[i]['ticker'])
        }}      
      }
      this.setState({ exchange: copyExchange, ready_exchange: true });
    });
  }

  updateStock(socket) {

    console.log('updateStock');
    socket.on('UPDATE',(data) => {
      if (this.state.ready_stock && this.state.ready_exchange){
        var date = new Date(data['time'] * 1000)
        var hours = date.getHours()
        var minutes = "0" + date.getMinutes()
        var formattedTime = hours + ':' + minutes.substr(-2)
        let copyStocks = { ...this.state.stocks };
        copyStocks[data['ticker']]['data'].push({'time':formattedTime, value :data['value']});
        if (copyStocks[data['ticker']]['lower']=== 0 || copyStocks[data['ticker']]['lower'] > data['value']){
          copyStocks[data['ticker']]['lower'] = data['value']
        }
        if (copyStocks[data['ticker']]['higher'] < data['value']){
          copyStocks[data['ticker']]['higher'] = data['value']
        }
        copyStocks[data['ticker']]['porcentual_change'] = Math.round(((data['value']*100/copyStocks[data['ticker']]['last_price'])-100) * 100) / 100
        copyStocks[data['ticker']]['last_price'] = data['value']
        this.setState({ stocks: copyStocks })
      }
    });
  }

  tickerBuy(socket) {
    socket.on('BUY',(data) => {
      if (this.state.ready_stock && this.state.ready_exchange){
        let copyStocks = {...this.state.stocks }
        let copyExchange = { ...this.state.exchange }
        let market_total = 0
        copyStocks[data['ticker']]['total_volume'] = data['volume'] + copyStocks[data['ticker']]['total_volume']
        for (const ex in copyExchange){
          if (copyExchange[ex]['company_ticker'].indexOf(data['ticker']) >= 0){
            copyExchange[ex]['buy_volume'] = data['volume'] + copyExchange[ex]['buy_volume']
            copyExchange[ex]['total_volume'] = data['volume'] + copyExchange[ex]['total_volume']
          }
          market_total = market_total + copyExchange[ex]['total_volume']
        }
        for (const ex in copyExchange){
          copyExchange[ex]['participation'] = Math.round(((copyExchange[ex]['total_volume']*100)/market_total) * 100) / 100
        }
        this.setState({ stocks: copyStocks, exchange: copyExchange })
      }
    });
  }

  tickerSell(socket) {
    socket.on('SELL',(data) => {
      if (this.state.ready_stock && this.state.ready_exchange){
        let copyStocks = {...this.state.stocks }
        let copyExchange = {...this.state.exchange}
        let market_total = 0
        copyStocks[data['ticker']]['total_volume'] = data['volume'] + copyStocks[data['ticker']]['total_volume']
        for (const ex in  copyExchange){
          if (copyExchange[ex]['company_ticker'].indexOf(data['ticker']) >= 0){
            copyExchange[ex]['sell_volume'] = data['volume'] + copyExchange[ex]['sell_volume']
            copyExchange[ex]['total_volume'] = data['volume'] + copyExchange[ex]['total_volume']
          }
          market_total = market_total + copyExchange[ex]['total_volume']
        }
        for (const ex in copyExchange){
          copyExchange[ex]['participation'] = Math.round(((copyExchange[ex]['total_volume']*100)/market_total) * 100) / 100
        }
        this.setState({ stocks: copyStocks, exchange: copyExchange })
      }
    });
  }


  componentDidMount() {
    var socket = io('wss://le-18262636.bitzonte.com', {
      path: '/stocks'
    });
    this.getInitialStock(socket);
    this.getInitialExchange(socket);
    this.updateStock(socket);
    this.tickerSell(socket);
    this.tickerBuy(socket);
 }


  render () {
    // console.log(this.state.stocks)
    if (this.state.ready_stock && this.state.ready_exchange) {
      const { stocks } = this.state;
      const { exchange } = this.state;
      console.log(stocks);
      return (
        <div className="container">
          {/* <Grid container spacing={3} justify="center" alignItems="center"> */}
            {
              Object.keys(exchange).map((key, index) => (
                <GridList cellHeight={50} cols={3}>
                  <p key={index}>{exchange[key]['exchange_name']}</p>
                  <Table className="table" size="small" aria-label="a dense table">
                    <TableHead>
                      <TableRow>
                        <TableCell align="center">Volumen Compra</TableCell>
                        <TableCell align="center">Volumen Venta</TableCell>
                        <TableCell align="center">Volumen Total</TableCell>
                        <TableCell align="center">Cantidad Acciones</TableCell>
                        <TableCell align="center">Participación Mercado</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      <TableRow>
                        <TableCell align="center">{exchange[key]['buy_volume']}</TableCell>
                        <TableCell align="center">{exchange[key]['sell_volume']}</TableCell>
                        <TableCell align="center">{exchange[key]['total_volume']}</TableCell>
                        <TableCell align="center">{exchange[key]['quantity']}</TableCell>
                        <TableCell align="center">{exchange[key]['participation']}</TableCell>
                      </TableRow>
                    </TableBody>
                  </Table>
                  {
                    Object.values(exchange[key]['company_ticker']).map((ticker, j) => (
                      <GridListTile key={j} rows={10}>
                        <p key={j}>{stocks[ticker]['company_name']}</p>
                        <LineChart
                          width={500}
                          height={200}
                          data={[...stocks[ticker]['data']]}
                          margin={{
                            top: 10, right: 30, left: 0, bottom: 0,
                          }}
                        >
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="time" />
                        <YAxis />
                        <Tooltip />
                        <Line type="monotone" dataKey='value' stroke="#8884d8" fill="#8884d8" />
                        </LineChart>
                        <Table className="table" size="small" aria-label="a dense table">
                          <TableHead>
                            <TableRow>
                              <TableCell align="center">Volumen Total Transado</TableCell>
                              <TableCell align="center">Alto Histórico</TableCell>
                              <TableCell align="center">Bajo Histórico</TableCell>
                              <TableCell align="center">Último Precio</TableCell>
                              <TableCell align="center">Cambio Porcentual</TableCell>
                            </TableRow>
                          </TableHead>
                          <TableBody>
                            <TableRow>
                              <TableCell align="center">{stocks[ticker]['total_volume']}</TableCell>
                              <TableCell align="center">{stocks[ticker]['higher']}</TableCell>
                              <TableCell align="center">{stocks[ticker]['lower']}</TableCell>
                              <TableCell align="center">{stocks[ticker]['last_price']}</TableCell>
                              <TableCell align="center">{stocks[ticker]['porcentual_change']}</TableCell>
                            </TableRow>
                          </TableBody>
                        </Table>
                      </GridListTile>
                      ))
                    }
                </GridList>
              ))
            }
          {/* </Grid> */}
        </div>
      );
    } else { 
      return( 
        <div className="App">
          <h1>Acción</h1>
        </div>
      ); 
    }
  }

} 

export default App;
