import React from 'react';
import './App.css';
import * as io from 'socket.io-client';
//import { useEffect, useState } from 'react';

const socket = io('wss://le-18262636.bitzonte.com', {
  path: '/stocks'
});

class App extends React.Component{
  constructor() {
    super();

    this.state = {
      exchange: {},
      stocks: {},
      ready: false
    };
  }

  getInitialStock() {
    let copyStocks = {};
    socket.emit('STOCKS');
    socket.on('STOCKS', (data) => {
      for (const num in data) {
        copyStocks[data[num]['ticker']] = {
          'company_name': data[num]['company_name'],
          'country': data[num]['country'],
          'total_volume': 0,
          'higher': 0,
          'lower': 0,
          'last_price': 0,
          'porentual_change': 0,
          'time': [],
          'price': []
        };
      }
    });
    this.setState({ stocks: copyStocks, ready: true });
    //this.updateStock(socket)
  }

  updateStock() {
    let copyStocks = { ...this.state.stocks };
    console.log('updateStock');
    console.log(this.state.stocks);
    socket.on('UPDATE',(data) => {
      //console.log(data);
      copyStocks[data['ticker']]['price'].push(data['value']);
      copyStocks[data['ticker']]['time'].push(data['time']);
      this.setState({ stocks: copyStocks });
    });
    //.push(data['time']) ;
    //copyStocks[data['ticker']]['price'].push(data['value']) ;
    //this.setState({ stocks: copyStocks });
    console.log(this.state.stocks);
  }

  componentDidMount() {
    this.getInitialStock(socket);
    if (this.ready){
      this.updateStock(socket);
    }
    // socket.emit('EXCHANGES');
    //socket.on('EXCHANGES', (data) => {
     // console.log(data);
      //this.setState({exchange: data})
    //});

    //console.log(this.state.stocks['AAPL']['time']);
    // {ticker: "SNAP", time: 1589552842327, value: 11.28}
   //
  }

  render () {

    if (this.ready){
      this.updateStock(socket);
    }
    console.log(this.stocks)

    return (
      <div className="App">
        <h1>Acci√≥n</h1>
      </div>
    );
  }
} 

export default App;
